import React, { useEffect, useState } from 'react';
import { useRouteMatch, Link } from 'react-router-dom';
import { FiChevronLeft, FiChevronRight, FiUsers } from 'react-icons/fi';
import Modal from 'react-bootstrap/Modal';

import api, { my_api } from '../../services/api';

import logoImg from '../../assets/trybe.png';

import { Header, RepositoryInfo, Issues, TableContainer } from './styles';

interface RepositoryParams {
  repository: string;
}

interface Repository {
  id: number;
  full_name: string;
  name: string;
  description: string;
  stargazers_count: number;
  forks_count: number;
  open_issues_count: number;
  owner: {
    login: string;
    avatar_url: string;
  };
}

interface PullRequest {
  title: string;
  id: number;
  html_url: string;
  mergeable_state: string | undefined;
  user: {
    login: string;
    url: string;
    avatar_url: string;
  };
  number: number;
  labels: [
    {
      name: string;
    },
  ];
  codeReview: boolean;
  helpWanted: boolean;
  jaTemDupla: boolean;
}

interface Dupla {
  dupla1: string;
  dupla2: string;
}

const Repository: React.FC = () => {
  const { params } = useRouteMatch<RepositoryParams>();

  const [repository, setRepository] = useState<Repository | null>(null);
  const [pullRequests, setPullRequests] = useState<PullRequest[]>([]);
  const [helpNumber, setHelpNumber] = useState(0);
  const [crNumber, setCrNumber] = useState(0);

  const [duplas, setDuplas] = useState<Dupla[]>([]);

  const [dataToShow, setDataToShow] = useState<PullRequest[]>([]);
  const [showModal, setShowModal] = useState<Boolean>(false);

  useEffect(() => {
    api.get(`repos/${params.repository}`).then((response) => {
      setRepository(response.data);
    });
    api
      .get(`repos/${params.repository}/pulls?per_page=100`)
      .then((response) => {
        setPullRequests(response.data);
      });
  }, [params.repository]);
  useEffect(() => {
    let helps = 0;
    let crs = 0;
    for (let pull of pullRequests) {
      pull.helpWanted = false;
      pull.codeReview = false;
      pull.jaTemDupla = false;
      if (pull.labels.length > 0) {
        for (let i = 0; i < pull.labels.length; i++) {
          if (pull.labels[i].name === 'help wanted') {
            helps++;
            pull.helpWanted = true;
          }
          if (pull.labels[i].name === 'code-review') {
            crs++;
            pull.codeReview = true;
          }
        }
      }
    }

    setDataToShow(pullRequests);
    setCrNumber(crs);
    setHelpNumber(helps);
  }, [pullRequests]);

  function shuffle(array: PullRequest[]) {
    let currentIndex = array.length,
      temporaryValue,
      randomIndex;

    while (0 !== currentIndex) {
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;

      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }

    return array;
  }

  async function updateDuplas() {
    const response = await my_api.get(`/${repository?.id}`);
    setDuplas(response.data);

    const prWithCr = pullRequests.filter((pull) => pull.codeReview);

    const strDuplas: string[] = [];
    response.data.forEach((dupla: Dupla) => {
      strDuplas.push(dupla.dupla1);
      strDuplas.push(dupla.dupla2);
    });

    const uniquesPr = prWithCr.filter(
      (pull) => !strDuplas.find((nome) => nome === pull.user.login),
    );

    let randomUniquePr = uniquesPr;
    randomUniquePr.sort((a, b) => a.id - b.id);

    console.log(uniquesPr[1].user.login);
    console.log(randomUniquePr[1].user.login);

    for (let i = 0; i < prWithCr.length; i++) {
      // while (uniquesPr[i]?.user.login === randomUniquePr[i]?.user.login) {
      //   shuffle(randomUniquePr);
      // }
      // await my_api.post(`/${repository?.id}`, {
      //   dupla1: uniquesPr[i].user.login,
      //   dupla2: randomUniquePr[i].user.login,
      // });
    }
  }

  async function handleDuplasButton() {
    await updateDuplas();
    handleOpenModal();
    console.log(repository?.id);
  }

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  function handleHelpButton() {
    const newPullRequests = pullRequests.filter((pull) => pull.helpWanted);
    setDataToShow(newPullRequests);
  }
  function handleCRButton() {
    const newPullRequests = pullRequests.filter((pull) => pull.codeReview);
    setDataToShow(newPullRequests);
  }
  function handlePRButton() {
    setDataToShow(pullRequests);
  }

  return (
    <>
      <Header>
        <img src={logoImg} width={150} alt="Trybe - Projects Explorer" />
        <Link to="/trybe-projects">
          <FiChevronLeft size={16} />
          Voltar
        </Link>
      </Header>

      {repository && (
        <RepositoryInfo>
          <header>
            <img
              src={repository.owner.avatar_url}
              alt={repository.owner.login}
            />
            <div>
              <strong>{repository.name}</strong>
            </div>
            <button onClick={handleDuplasButton}>
              <FiUsers size={24} />
              Ver duplas de CR
            </button>
          </header>
          <ul>
            <li>
              <Link to="#" onClick={handleHelpButton}>
                <strong>{helpNumber}</strong>
                <span>Pedido(s) de ajuda</span>
              </Link>
            </li>
            <li>
              <Link to="#" onClick={handleCRButton}>
                <strong>{crNumber}</strong>
                <span>Pedido(s) de Code Review</span>
              </Link>
            </li>
            <li>
              <Link to="#" onClick={handlePRButton}>
                <strong>{repository.open_issues_count}</strong>
                <span>Pull Requests</span>
              </Link>
            </li>
          </ul>
        </RepositoryInfo>
      )}

      <Modal show={showModal} onHide={handleCloseModal} size={'lg'}>
        <Modal.Body>
          <TableContainer>
            <h1>Duplas de Code Review</h1>
            {duplas.map((dupla, index) => (
              <section key={dupla.dupla1}>
                <div>
                  <p>{dupla.dupla1}</p>
                </div>
                <div>
                  <strong>-></strong>
                </div>
                <div>
                  <p>{dupla.dupla2}</p>
                </div>
              </section>
            ))}
          </TableContainer>
        </Modal.Body>
      </Modal>

      <Issues>
        {dataToShow
          .filter((pull) => {
            return pull.user.login !== 'tryber';
          })
          .sort((a, b): number => {
            if (a.codeReview && !b.codeReview) {
              return -1;
            }
            if (a.helpWanted && !b.helpWanted) {
              return -1;
            }
            return 1;
          })
          .map((pull) => (
            <a key={pull.id} href={pull.html_url}>
              <img src={pull.user.avatar_url} alt={pull.user.login} />
              <div>
                <strong>{pull.user.login}</strong>
                <p>{pull.title}</p>
              </div>
              {pull.helpWanted && <span className="red">Ajuda</span>}
              {pull.codeReview && <span className="green">Code Review</span>}
              <FiChevronRight size={20} />
            </a>
          ))}
      </Issues>
    </>
  );
};

export default Repository;
