import React, { useState, useEffect, FormEvent } from 'react';
import { FiChevronRight } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import api from '../../services/api';

import logoImg from '../../assets/logo.svg';

import { Title, Form, Repositories, Error } from './styles';

interface Repository {
  full_name: string;
  description: string;
  owner: {
    login: string;
    avatar_url: string;
  };
}

const Dashboard: React.FC = () => {
  const [newRepoName, setNewRepoName] = useState('');
  const [inputErrorName, setInputErrorName] = useState('');
  const [repositories, setRepositories] = useState<Repository[]>(() => {
    const storagedRepositories = localStorage.getItem(
      '@GithubExplorer:repositories',
    );
    if (storagedRepositories) {
      return JSON.parse(storagedRepositories);
    }
    return [];
  });

  useEffect(() => {
    localStorage.setItem(
      '@GithubExplorer:repositories',
      JSON.stringify(repositories),
    );
  }, [repositories]);

  async function handleAddRepository(
    event: FormEvent<HTMLFormElement>,
  ): Promise<boolean> {
    event.preventDefault();

    setInputErrorName('');

    if (!newRepoName) {
      setInputErrorName('Digite o nome do repositório!');
      return false;
    }

    try {
      const response = await api.get<Repository>(
        `repos/tryber/${newRepoName}`,
      );
      const repository = response.data;

      setRepositories([...repositories, repository]);
      setNewRepoName('');
      setInputErrorName('');
    } catch (e) {
      setInputErrorName('Erro! Projeto não encontrado, por favor, digite o nome do repositório do projeto completo');
    }
    return true;
  }

  return (
    <>
      <img src={logoImg} alt="Github Explorer" />
      <Title>Acompanhe os projetos Trybe</Title>

      <Form
        hasError={!!(inputErrorOwner || inputErrorName)}
        onSubmit={handleAddRepository}
      >
        <input
          value={newRepoName}
          onChange={(e) => setNewRepoName(e.target.value)}
          placeholder="Digite o nome do repositório do projeto"
          type="text"
        />
        <button type="submit">Pesquisar</button>
      </Form>

      {inputErrorName && <Error>{inputErrorName}</Error>}

      <Repositories>
        {repositories.map((repository) => (
          <Link
            key={repository.full_name}
            to={`/repository/${repository.full_name}`}
          >
            <img
              src={repository.owner.avatar_url}
              alt={repository.owner.login}
            />
            <div>
              <strong>{repository.full_name}</strong>
              <p>{repository.description}</p>
            </div>

            <FiChevronRight size={20} />
          </Link>
        ))}
      </Repositories>
    </>
  );
};

export default Dashboard;
