import React, { useState, useEffect, FormEvent } from 'react';
import { FiChevronRight, FiX } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import api from '../../services/api';

import logoImg from '../../assets/logo.svg';

import { Title, Form, Repositories, Error, Repository } from './styles';

interface Repository {
  full_name: string;
  name: string;
  description: string;
  owner: {
    login: string;
    avatar_url: string;
  };
}

const Dashboard: React.FC = () => {
  const [team, setTeam] = useState('');
  const [newRepoName, setNewRepoName] = useState('');
  const [inputError, setInputError] = useState('');
  const [repositories, setRepositories] = useState<Repository[]>(() => {
    const storagedRepositories = localStorage.getItem(
      '@GithubExplorer:repositories',
    );
    if (storagedRepositories) {
      return JSON.parse(storagedRepositories);
    }
    return [];
  });

  useEffect(() => {
    localStorage.setItem(
      '@GithubExplorer:repositories',
      JSON.stringify(repositories),
    );
  }, [repositories]);

  async function handleAddRepository(
    event: FormEvent<HTMLFormElement>,
  ): Promise<boolean> {
    setInputError('');

    if (!newRepoName) {
      setInputError('Digite o nome do repositório do projeto!');
      return false;
    }

    try {
      const response = await api.get<Repository>(`repos/tryber/${newRepoName}`);
      const repository = response.data;

      setRepositories([...repositories, repository]);
      setNewRepoName('');
      setInputError('');
    } catch (e) {
      setInputError(
        'Erro! Turma não encontrada, por favor, digite o número da turma (1-5).',
      );
    }
    return true;
  }

  async function handleDeleteRepository(repo: Repository): Promise<void> {
    const newRepositories = await repositories.filter(
      (rep) => rep.full_name !== repo.full_name,
    );
    localStorage.setItem(
      '@GithubExplorer:repositories',
      JSON.stringify(newRepositories),
    );
    setRepositories(newRepositories);
  }
  return (
    <>
      <img src={logoImg} alt="Github Explorer" />
      <Title>Acompanhe os projetos Trybe</Title>

      <Form hasError={!!inputError}>
        <input
          value={team}
          onChange={(e) => setTeam(e.target.value)}
          placeholder="Digite a turma: (ex.: 1)"
          type="number"
        />
        <button type="submit">Pesquisar</button>
      </Form>

      {inputError && <Error>{inputError}</Error>}

      <Repositories>
        {repositories.map((repository) => (
          <Repository>
            <button onClick={() => handleDeleteRepository(repository)}>
              <FiX size={30} />
            </button>
            <Link
              key={repository.full_name}
              to={`/repository/${repository.full_name}`}
            >
              <img
                src={repository.owner.avatar_url}
                alt={repository.owner.login}
              />
              <div>
                <strong>{repository.name}</strong>
                <p>{repository.description}</p>
              </div>
            </Link>
            <FiChevronRight size={20} />
          </Repository>
        ))}
      </Repositories>
    </>
  );
};

export default Dashboard;
