import React, { useEffect, useState } from 'react';
import { useRouteMatch, Link } from 'react-router-dom';
import {
  FiChevronLeft,
  FiUsers,
  FiPlusCircle,
  FiRefreshCcw,
} from 'react-icons/fi';
import Modal from 'react-bootstrap/Modal';

import api, { my_api } from '../../services/api';

import logoImg from '../../assets/trybe.png';

import { Header, RepositoryInfo, TableContainer } from './styles';

interface RepositoryParams {
  repository: string;
}

interface Repository {
  id: number;
  full_name: string;
  name: string;
  description: string;
  stargazers_count: number;
  forks_count: number;
  open_issues_count: number;
  owner: {
    login: string;
    avatar_url: string;
  };
}

interface PullRequest {
  title: string;
  id: number;
  html_url: string;
  mergeable_state: string | undefined;
  user: {
    login: string;
    url: string;
    avatar_url: string;
  };
  number: number;
  labels: [
    {
      name: string;
    },
  ];
  codeReview: boolean;
  helpWanted: boolean;
  jaTemDupla: boolean;
}

interface Dupla {
  dupla1: string;
  dupla2: string;
}

const AdminRepository: React.FC = () => {
  const { params } = useRouteMatch<RepositoryParams>();

  const [repository, setRepository] = useState<Repository | null>(null);
  const [pullRequests, setPullRequests] = useState<PullRequest[]>([]);

  const [duplas, setDuplas] = useState<Dupla[]>([]);

  const [showModal, setShowModal] = useState<Boolean>(false);

  // useEffect(() => {
  //   api.get(`repos/${params.repository}`).then((response) => {
  //     setRepository(response.data);
  //   });
  //   api
  //     .get(`repos/${params.repository}/pulls?per_page=100`)
  //     .then((response) => {
  //       setPullRequests(response.data);
  //     });
  // }, [params.repository]);

  useEffect(() => {
    if (repository) {
      my_api.get(`/${repository?.id}`).then((response) => {
        setDuplas(response.data);
      });
    }
  }, [repository, pullRequests]);

  async function createDuplas() {
    const prWithCr = pullRequests.filter((pull) => pull.codeReview);
    const namesOnPrWithCr = prWithCr.map((pull) => pull.user.login);
    my_api.post(`/${repository?.id}`, namesOnPrWithCr);
  }

  async function handleDuplasButton() {
    createDuplas();
    handleOpenModal();
  }

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  return (
    <>
      <Header>
        <img src={logoImg} width={150} alt="Trybe - Projects Explorer" />
        <Link to="/trybe-projects">
          <FiChevronLeft size={16} />
          Voltar
        </Link>
      </Header>

      {repository && (
        <RepositoryInfo>
          <header>
            {/* <img
              src={repository.owner.avatar_url}
              alt={repository.owner.login}
            /> */}
            <div>
              <strong>aaa</strong>
            </div>
          </header>
          <ul>
            <li>
              <Link to="#">
                <FiPlusCircle size={20} />
                <span>Gerar duplas</span>
              </Link>
            </li>
            <li>
              <Link to="#">
                <FiRefreshCcw size={20} />
                <span>Atualizar duplas</span>
              </Link>
            </li>
            <li>
              <Link to="#">
                <FiUsers size={20} />
                <span>Ver duplas</span>
              </Link>
            </li>
          </ul>
        </RepositoryInfo>
      )}

      <Modal show={showModal} onHide={handleCloseModal} size={'lg'}>
        <Modal.Body>
          <TableContainer>
            <h1>Duplas de Code Review</h1>
            {duplas.map((dupla, index) => (
              <section key={dupla.dupla1}>
                <p>{dupla.dupla1}</p>
                <strong>-></strong>
                <p>{dupla.dupla2}</p>
              </section>
            ))}
          </TableContainer>
        </Modal.Body>
      </Modal>
    </>
  );
};

export default AdminRepository;
