import React, { useEffect, useState } from 'react';
import { useRouteMatch, Link } from 'react-router-dom';
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import api from '../../services/api';

import logoImg from '../../assets/trybe.png';

import { Header, RepositoryInfo, Issues } from './styles';

interface RepositoryParams {
  repository: string;
}

interface Repository {
  full_name: string;
  name: string;
  description: string;
  stargazers_count: number;
  forks_count: number;
  open_issues_count: number;
  owner: {
    login: string;
    avatar_url: string;
  };
}

interface PullRequest {
  title: string;
  id: number;
  html_url: string;
  user: {
    login: string;
    url: string;
    avatar_url: string;
  };
}

interface User {
  login: string;
  name: string;
}

const Repository: React.FC = () => {
  const { params } = useRouteMatch<RepositoryParams>();

  const [repository, setRepository] = useState<Repository | null>(null);
  const [pullRequests, setPullRequests] = useState<PullRequest[]>([]);

  useEffect(() => {
    api.get(`repos/${params.repository}`).then((response) => {
      setRepository(response.data);
    });
    api.get(`repos/${params.repository}/pulls`).then((response) => {
      setPullRequests(response.data);
    });
  }, [params.repository]);

  return (
    <>
      <Header>
        <img src={logoImg} width={200} alt="Github Explorer" />
        <Link to="/">
          <FiChevronLeft size={16} />
          Voltar
        </Link>
      </Header>

      {repository && (
        <RepositoryInfo>
          <header>
            <img
              src={repository.owner.avatar_url}
              alt={repository.owner.login}
            />
            <div>
              <strong>{repository.name}</strong>
              <p>{repository.description}</p>
            </div>
          </header>
          <ul>
            <li>
              <strong>{repository.stargazers_count}</strong>
              <span>Stars</span>
            </li>
            <li>
              <strong>{repository.forks_count}</strong>
              <span>Forks</span>
            </li>
            <li>
              <strong>{repository.open_issues_count}</strong>
              <span>Pull Requests</span>
            </li>
          </ul>
        </RepositoryInfo>
      )}

      <Issues>
        {pullRequests
          .filter((pull) => {
            return pull.user.login !== 'tryber';
          })
          .map((pull) => (
            <a key={pull.id} href={pull.html_url}>
              <img
                src={pull.user.avatar_url}
                alt={pull.user.login}
              />
              <div>
                <strong>{pull.user.login}</strong>
                <p>{pull.title} </p>
              </div>

              <FiChevronRight size={20} />
            </a>
          ))}
      </Issues>
    </>
  );
};

export default Repository;
