import React, { useEffect, useState } from 'react';
import { useRouteMatch, Link } from 'react-router-dom';
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import api from '../../services/api';

import logoImg from '../../assets/trybe.png';

import { Header, RepositoryInfo, Issues } from './styles';

interface RepositoryParams {
  repository: string;
}

interface Repository {
  full_name: string;
  name: string;
  description: string;
  stargazers_count: number;
  forks_count: number;
  open_issues_count: number;
  owner: {
    login: string;
    avatar_url: string;
  };
}

interface PullRequest {
  title: string;
  id: number;
  html_url: string;
  mergeable_state: string | undefined;
  user: {
    login: string;
    url: string;
    avatar_url: string;
  };
  number: number;
  labels: [
    {
      name: string;
    },
  ];
}

interface User {
  login: string;
  name: string;
}

const Repository: React.FC = () => {
  const { params } = useRouteMatch<RepositoryParams>();

  const [repository, setRepository] = useState<Repository | null>(null);
  const [pullRequests, setPullRequests] = useState<PullRequest[]>([]);
  const [helpNumber, setHelpNumber] = useState(0);

  function updateHelpNumber() {
    let helps=0;
    for(const pull in pullRequests) {
      if(pull.labels.length>0){
        for(const label of pull.labels){
          if(label.name==='help wanted'){
            helps++;
          }
        }
      }
    }
    setHelpNumber(helps);
  }

  useEffect(() => {
    api.get(`repos/${params.repository}`).then((response) => {
      setRepository(response.data);
    });
    api
      .get(`repos/${params.repository}/pulls?per_page=100`)
      .then((response) => {
        setPullRequests(response.data);
      });
    updateHelpNumber();
  }, [params.repository]);

  console.log(pullRequests);

  return (
    <>
      <Header>
        <img src={logoImg} width={150} alt="Github Explorer" />
        <Link to="/">
          <FiChevronLeft size={16} />
          Voltar
        </Link>
      </Header>

      {repository && (
        <RepositoryInfo>
          <header>
            <img
              src={repository.owner.avatar_url}
              alt={repository.owner.login}
            />
            <div>
              <strong>{repository.name}</strong>
              <p>{repository.description}</p>
            </div>
          </header>
          <ul>
            <li>
              <strong>{repository.stargazers_count}</strong>
              <span>Stars</span>
            </li>
            <li>
              <strong>{repository.forks_count}</strong>
              <span>Forks</span>
            </li>
            <li>
              <strong>{repository.open_issues_count}</strong>
              <span>Pull Requests</span>
            </li>
          </ul>
        </RepositoryInfo>
      )}

      <Issues>
        {pullRequests
          .filter((pull) => {
            return pull.user.login !== 'tryber';
          })
          .map((pull) => (
            <a key={pull.id} href={pull.html_url}>
              <img src={pull.user.avatar_url} alt={pull.user.login} />
              <div>
                <strong>{pull.user.login}</strong>
                <p>{pull.title}</p>
              </div>
              {pull.labels.length>0 && pull.labels[0].name==='help wanted' &&<p>Ajuda!</p>}
              <FiChevronRight size={20} />
            </a>
          ))}
      </Issues>
    </>
  );
};

export default Repository;
